// ============================================================
// CONFIGURATION — Fill in all values below for your project
// ============================================================

const config = {
  // ----- Linear API -----
  // Your Linear API key (create at https://linear.app/settings/api)
  // Format: "lin_api_..."
  linearApiKey: "lin_api_XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX",

  // ----- Linear IDs -----
  // Your Linear team UUID (see setup.md for how to find this)
  teamId: "xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx",

  // The UUID of your "Ready" (or "Todo") workflow state
  // This is the state that issues must be in to be picked up
  readyStateId: "xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx",

  // The UUID of your "Blocked" workflow state
  // Issues that fail or time out will be moved here
  blockedStateId: "xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx",

  // The UUID of your "Done" workflow state
  doneStateId: "xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx",

  // ----- Project -----
  // Absolute path to the target project on the n8n host machine
  projectPath: "/home/user/my-project",

  // Human-readable project name (used in prompts)
  projectName: "my-project",

  // Tech stack description (used in prompts)
  techStack: "TypeScript, Next.js, Prisma, PostgreSQL",

  // Test command to run during validation
  testCommand: "npm test",

  // Lint command to run during validation
  lintCommand: "npm run lint",

  // ----- Executor Settings -----
  // Maximum number of issues to execute per run (fan-out parallelism)
  maxParallel: 3,

  // Timeout in seconds for each Claude Code invocation
  // 30 minutes = 1800 seconds
  timeoutSeconds: 1800,

  // Absolute path to the claude-autopilot directory
  autopilotPath: "/home/user/claude-autopilot",
};

return [{ json: config }];

