={
  "query": "query ReadyIssues($teamId: String!, $stateId: String!) { issues( filter: { team: { id: { eq: $teamId } } state: { id: { eq: $stateId } } } orderBy: priority first: 10 ) { nodes { id identifier title description priority labels { nodes { name } } relations { nodes { type relatedIssue { id identifier state { name type } } } } } } }",
  "variables": {
    "teamId": "{{ $json.teamId }}",
    "stateId": "{{ $json.readyStateId }}"
  }
}

parameters.jsCode:
// Retrieve config from the Set Config node
const config = $('Set Config').first().json;

// Get raw issues from Linear GraphQL response
const responseData = $input.first().json;
const issues = responseData?.data?.issues?.nodes ?? [];

if (issues.length === 0) {
  return [];
}

// Filter to only unblocked issues:
// An issue is "blocked" if it has a relation of type "blocks" where the
// related issue's state type is NOT "completed" or "canceled".
const unblockedIssues = issues.filter(issue => {
  const relations = issue.relations?.nodes ?? [];
  for (const rel of relations) {
    // "blocks" type means: the related issue blocks THIS issue
    // We check if any blocking relation points to an incomplete issue
    if (rel.type === 'blocks') {
      const relatedState = rel.relatedIssue?.state;
      if (relatedState && relatedState.type !== 'completed' && relatedState.type !== 'canceled') {
        return false; // This issue is blocked
      }
    }
  }
  return true; // No incomplete blocking relations
});

// Take top N by priority (already sorted by Linear)
const maxParallel = config.maxParallel || 3;
const selected = unblockedIssues.slice(0, maxParallel);

// Return each issue as a separate item for fan-out execution
return selected.map(issue => ({
  json: {
    ...issue,
    // Pass config through so downstream nodes can use it
    _config: config,
  }
}));

// Collect results from all Claude Code executions
const items = $input.all();

let successCount = 0;
let failCount = 0;
let timeoutCount = 0;
const results = [];

for (const item of items) {
  const stdout = item.json.stdout ?? '';
  const exitCode = item.json.exitCode ?? -1;
  const identifier = item.json.identifier ?? 'unknown';
  const title = item.json.title ?? '';

  let status = 'unknown';
  if (exitCode === 0) {
    status = 'success';
    successCount++;
  } else if (exitCode === 124) {
    status = 'timeout';
    timeoutCount++;
  } else {
    status = 'failed';
    failCount++;
  }

  results.push({
    identifier,
    title,
    status,
    exitCode,
  });
}

const summary = {
  totalProcessed: items.length,
  success: successCount,
  failed: failCount,
  timeout: timeoutCount,
  results,
  timestamp: new Date().toISOString(),
};

console.log(`[executor-summary] Processed: ${items.length}, Success: ${successCount}, Failed: ${failCount}, Timeout: ${timeoutCount}`);

return [{ json: summary }];
